import React, { useState } from "react";
import Papa from "papaparse";
import { parse, isValid, format } from "date-fns";

function App() {
  const [file, setFile] = useState(null); // Pour stocker le fichier sélectionné
  const [data, setData] = useState([]); // Pour stocker les données du CSV

  // Fonction pour gérer la sélection du fichier
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile); // Met à jour l'état avec le fichier sélectionné

    // Lire le fichier avec PapaParse
    if (selectedFile) {
      Papa.parse(selectedFile, {
        complete: (result) => {
          console.log("Données CSV analysées :", result.data);
          const formattedData = result.data.map((row) => ({
            nom: row["Nom"]?.trim().replace(/"/g, ""), // Nettoyer les guillemets
            prenom: row["Prenom"]?.trim().replace(/"/g, ""), // Nettoyer les guillemets
            dateDeNaissance: row["Date de naissance"]?.trim().replace(/"/g, ""), // Nettoyer les guillemets
          }));
          setData(formattedData); // Mettre à jour l'état avec les données extraites

          // Envoi des données au backend
          sendDataToBackend(formattedData);
        },
        header: true, // Utiliser la première ligne comme entête
        skipEmptyLines: true, // Ignorer les lignes vides
      });
    }
  };

  
  // Fonction pour envoyer les données au backend
  const sendDataToBackend = async (students) => {
    try {
      const studentsWithDate = students.map((student) => {
        let dateDeNaissance = student.dateDeNaissance;
  
        // Vérifier et nettoyer la date
        // Si la date est au format ISO 8601 (yyyy-MM-dd), la parser directement
        const parsedDate = parse(dateDeNaissance, 'yyyy-MM-dd', new Date());
  
        if (isValid(parsedDate)) {
          // Si la date est valide, la convertir en ISO 8601 avec le bon format
          dateDeNaissance = format(parsedDate, "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'");
        } else {
          // Si la date est invalide ou vide, la mettre à null
          dateDeNaissance = null; // Vous pouvez également remplacer par une date par défaut si nécessaire
        }
  
        return dateDeNaissance ? {
          ...student,
          dateDeNaissance, // Remplacer par la date formatée
        } : null;
      }).filter(student => student !== null); // Filtrer les étudiants sans date valide
  
      if (studentsWithDate.length === 0) {
        alert("Aucun élève avec une date valide n'a été trouvé.");
        return;
      }
  
      const response = await fetch("http://localhost:5000/api/students", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ students: studentsWithDate }),
      });
  
      const data = await response.json();
  
      if (response.ok) {
        alert("Les élèves ont été enregistrés dans la base de données.");
      } else {
        alert("Erreur lors de l'enregistrement des élèves :", data.message);
      }
    } catch (error) {
      console.error("Erreur d'envoi au serveur:", error);
      alert("Une erreur est survenue lors de l'enregistrement des élèves.");
    }
  };

  return (
    <div className="App">
      <h1>Importer un fichier CSV</h1>

      {/* Bouton pour sélectionner le fichier CSV */}
      <input
        type="file"
        accept=".csv"
        onChange={handleFileChange}
        className="border p-2 m-4"
      />

      {/* Affichage du contenu du CSV sous forme de tableau */}
      {data.length > 0 && (
        <table className="table-auto border-collapse border border-gray-400">
          <thead>
            <tr>
              {Object.keys(data[0]).map((header, index) => (
                <th key={index} className="border border-gray-300 px-4 py-2">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {Object.values(row).map((cell, cellIndex) => (
                  <td
                    key={cellIndex}
                    className="border border-gray-300 px-4 py-2"
                  >
                    {cell}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
