import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleImport = async () => {
    if (!file) return alert("Veuillez sélectionner un fichier");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:5000/import", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        alert("Import réussi !");
        console.log(data.students);
      } else {
        alert("Erreur : " + data.message);
      }
    } catch (error) {
      console.error("Erreur lors de l'import :", error);
    }
  };

  return (
    <div className="font-sans bg-gray-100 min-h-screen">
      <nav className="bg-blue-600 p-4 text-white text-center">
        <label className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded-lg mx-2 cursor-pointer">
          Importer des élèves
          <input
            type="file"
            accept=".csv"
            className="hidden"
            onChange={handleFileChange}
          />
        </label>
        <button
          onClick={handleImport}
          className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded-lg mx-2"
        >
          Importer
        </button>
      </nav>
      {/* Tableau des élèves */}
    </div>
  );
}

export default App;
