import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null); // Pour stocker le fichier sélectionné
  const [data, setData] = useState([]); // Pour stocker les données du CSV

  // Fonction pour gérer la sélection du fichier
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile); // Met à jour l'état avec le fichier sélectionné

    // Lire le fichier avec FileReader
    if (selectedFile) {
      const reader = new FileReader();
      reader.onload = () => {
        const csvContent = reader.result; // Contenu du fichier CSV
        const parsedData = parseCSV(csvContent);
        setData(parsedData); // Mettre à jour l'état avec les données extraites
        // Envoi des données au backend
        sendDataToBackend(parsedData);
      };
      reader.readAsText(selectedFile); // Lire le fichier en tant que texte
    }
  };

  // Fonction pour parser le CSV (basé sur la séparation des lignes et des colonnes)
  const parseCSV = (csvContent) => {
    const rows = csvContent.split("\n"); // Séparer par ligne
    const headers = rows[0].split(","); // Séparer les en-têtes par virgule
    const data = rows.slice(1).map((row) => {
      const cells = row.split(","); // Séparer les cellules par virgule
      let rowData = {};
      headers.forEach((header, index) => {
        rowData[header.trim()] = cells[index]?.trim(); // Associer chaque valeur à l'en-tête
      });
      return rowData;
    });
    return data;
  };

  // Fonction pour envoyer les données au backend
  const sendDataToBackend = async (students) => {
    try {
      const response = await fetch("http://localhost:5000/api/students", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ students }),
      });

      const data = await response.json();

      if (response.ok) {
        alert("Les élèves ont été enregistrés dans la base de données.");
      } else {
        alert("Erreur lors de l'enregistrement des élèves.");
      }
    } catch (error) {
      console.error("Erreur d'envoi au serveur:", error);
      alert("Une erreur est survenue lors de l'enregistrement des élèves.");
    }
  };

  return (
    <div className="App">
      <h1>Importer un fichier CSV</h1>

      {/* Bouton pour sélectionner le fichier CSV */}
      <input
        type="file"
        accept=".csv"
        onChange={handleFileChange}
        className="border p-2 m-4"
      />

      {/* Affichage du contenu du CSV sous forme de tableau */}
      {data.length > 0 && (
        <table className="table-auto border-collapse border border-gray-400">
          <thead>
            <tr>
              {Object.keys(data[0]).map((header, index) => (
                <th key={index} className="border border-gray-300 px-4 py-2">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {Object.values(row).map((cell, cellIndex) => (
                  <td
                    key={cellIndex}
                    className="border border-gray-300 px-4 py-2"
                  >
                    {cell}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
