import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // Utilisation de useHistory pour la redirection

function Auth({ onLoginSuccess }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory(); // Hook pour gérer la redirection

  const handleLogin = async () => {
    try {
      console.log("Username:", email); // Affiche l'email fourni
      console.log("Password:", password); // Affiche le mot de passe fourni
      const response = await fetch("http://localhost:3000/api/auth", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username: email, password: password }), // Utilisez 'username' et 'password' pour correspondre au format de votre JSON utilisateur
      });

      if (response.ok) {
        const data = await response.json();
        onLoginSuccess(data.token); // Si vous recevez un jeton JWT, passez-le ici
        localStorage.setItem("token", data.token);
        localStorage.setItem("role", data.role); // Stockez le rôle
        alert("Connexion réussie");

        // Rediriger vers App.js après la connexion
        history.push("/app"); // Cette commande redirige vers App.js
      } else {
        const errorData = await response.json();
        alert(`Erreur : ${errorData.message}`);
      }
    } catch (error) {
      console.error("Erreur lors de la connexion :", error);
      alert("Une erreur est survenue lors de la tentative de connexion.");
    }
  };

  return (
    <div className="auth-container">
      <h2>Connexion</h2>
      <input
        type="text"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="input-field"
      />
      <input
        type="password"
        placeholder="Mot de passe"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="input-field"
      />
      <button onClick={handleLogin} className="login-button">
        Se connecter
      </button>
    </div>
  );
}

export default Auth;
