import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [students, setStudents] = useState([]); // État pour les élèves importés

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleImport = async () => {
    if (!file) {
      alert("Veuillez sélectionner un fichier");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    console.log("Fichier sélectionné :", file); 

    try {
      const response = await fetch("http://localhost:5000/import", {
        method: "POST",
        body: formData,
      });

      console.log("Réponse du serveur :", response);

      const data = await response.json();
      if (response.ok) {
        alert("Import réussi !");
        setStudents(data.students);
        console.log("Données reçues :", data.students); 
      } else {
        alert("Erreur : " + data.message);
      }
    } catch (error) {
      console.error("Erreur lors de l'import :", error);
    }
  };

  return (
    <div className="font-sans bg-gray-100 min-h-screen">
      <nav className="bg-blue-600 p-4 text-white text-center">
        <label className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded-lg mx-2 cursor-pointer">
          Importer des élèves
          <input
            type="file"
            accept=".csv"
            className="hidden"
            onChange={handleFileChange}
          />
        </label>
        <button
          onClick={handleImport}
          className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded-lg mx-2"
        >
          Importer
        </button>
      </nav>

      {/* Tableau des élèves */}
      <section className="p-6">
        <h2 className="text-3xl text-center text-gray-800 mb-6">
          Liste des élèves importés
        </h2>
        <div className="overflow-x-auto bg-white p-4 rounded-lg shadow-md">
          <table className="w-full text-left table-auto">
            <thead>
              <tr>
                <th className="py-2 px-4 text-gray-600">Nom</th>
                <th className="py-2 px-4 text-gray-600">Prénom</th>
                <th className="py-2 px-4 text-gray-600">Date de Naissance</th>
              </tr>
            </thead>
            <tbody>
              {students.length > 0 ? (
                students.map((student, index) => (
                  <tr key={index} className="hover:bg-gray-50">
                    <td className="py-2 px-4">{student.nom}</td>
                    <td className="py-2 px-4">{student.prenom}</td>
                    <td className="py-2 px-4">
                      {new Date(student.dateDeNaissance).toLocaleDateString()}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3" className="text-center py-4 text-gray-500">
                    Aucun élève importé
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </section>
    </div>
  );
}

export default App;
