import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import { parse, isValid, format } from "date-fns";

function App() {
  const [file, setFile] = useState(null);
  const [redoublantsFile, setRedoublantsFile] = useState(null); 
  const [data, setData] = useState([]);
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/classes");
      if (response.ok) {
        const classData = await response.json();

        // Trier les classes dans l'ordre désiré
        const order = [
          "Petite section",
          "Moyenne section",
          "Grande section",
          "CP",
          "CE1",
          "CE2",
          "CM1",
          "CM2",
        ];

        const sortedClasses = classData.sort(
          (a, b) => order.indexOf(a.classe) - order.indexOf(b.classe)
        );
        setClasses(sortedClasses);
      } else {
        console.error("Erreur lors de la récupération des classes");
      }
    } catch (error) {
      console.error("Erreur lors de la connexion au serveur :", error);
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    if (selectedFile) {
      Papa.parse(selectedFile, {
        complete: (result) => {
          const formattedData = result.data.map((row) => {
            const rawDate = row["Date de naissance"]?.trim();

            let dateDeNaissance = null;
            try {
              const parsedDate = parse(rawDate, "dd/MM/yyyy", new Date());
              if (isValid(parsedDate)) {
                dateDeNaissance = format(parsedDate, "yyyy-MM-dd");
              }
            } catch (error) {
              console.error("Erreur de format de date :", rawDate, error);
            }

            return {
              nom: row["Nom"]?.trim(),
              prenom: row["Prenom"]?.trim(),
              dateDeNaissance: dateDeNaissance,
            };
          });

          setData(formattedData);
          sendDataToBackend(formattedData);
        },
        header: true,
        skipEmptyLines: true,
      });
    }
  };

  const handleRedoublantsFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setRedoublantsFile(selectedFile);

    if (selectedFile) {
      Papa.parse(selectedFile, {
        complete: (result) => {
          const formattedData = result.data.map((row) => {
            const rawDate = row["Date de naissance"]?.trim();

            let dateDeNaissance = null;
            try {
              const parsedDate = parse(rawDate, "dd/MM/yyyy", new Date());
              if (isValid(parsedDate)) {
                dateDeNaissance = format(parsedDate, "yyyy-MM-dd");
              } else {
                console.error(`Date invalide détectée : ${rawDate}`);
              }
            } catch (error) {
              console.error("Erreur lors du parsing de la date :", rawDate, error);
            }

            return {
              nom: row["Nom"]?.trim(),
              prenom: row["Prenom"]?.trim(),
              dateDeNaissance: dateDeNaissance,
            };
          });

          sendRedoublantsToBackend(formattedData);
        },
        header: true,
        skipEmptyLines: true,
      });
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10">
      <header className="bg-blue-600 text-white p-6 text-center rounded-md shadow-md">
        <h1 className="text-3xl font-bold">Gestion des Élèves</h1>
        <p className="text-sm mt-2">Importez et gérez les données de vos élèves.</p>
      </header>

      <main className="mt-10 max-w-7xl mx-auto">
        {/* Encadrés côte à côte */}
        <div className="flex justify-between gap-6 mb-10">
          <section className="flex-1 bg-white p-6 rounded-md shadow-md">
            <h2 className="text-2xl font-semibold mb-4">Importer un fichier CSV</h2>
            <input
              type="file"
              accept=".csv"
              onChange={handleFileChange}
              className="hidden"
              id="file-upload"
            />
            <label
              htmlFor="file-upload"
              className="cursor-pointer bg-blue-600 text-white font-medium py-2 px-4 rounded-md shadow-md hover:bg-blue-700"
            >
              Choisir un fichier
            </label>
          </section>

          <section className="flex-1 bg-white p-6 rounded-md shadow-md">
            <h2 className="text-2xl font-semibold mb-4">Indiquer les redoublants</h2>
            <input
              type="file"
              accept=".csv"
              onChange={handleRedoublantsFileChange}
              className="hidden"
              id="redoublants-upload"
            />
            <label
              htmlFor="redoublants-upload"
              className="cursor-pointer bg-green-600 text-white font-medium py-2 px-4 rounded-md shadow-md hover:bg-green-700"
            >
              Choisir un fichier
            </label>
          </section>
        </div>

        {/* Tableaux en grille */}
        <div className="grid grid-cols-3 gap-6">
          {classes.map((classItem, classIndex) => (
            <div key={classIndex} className="bg-white p-4 rounded-md shadow-md">
              <h3 className="text-xl font-bold mb-2">{classItem.classe}</h3>
              {classItem.students.length > 0 ? (
                <table className="w-full border-collapse border border-gray-400">
                  <thead>
                    <tr>
                      <th className="border border-gray-300 px-4 py-2 bg-gray-100">Nom</th>
                      <th className="border border-gray-300 px-4 py-2 bg-gray-100">Prénom</th>
                      <th className="border border-gray-300 px-4 py-2 bg-gray-100">
                        Date de naissance
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {classItem.students.map((student, studentIndex) => (
                      <tr key={studentIndex}>
                        <td className="border border-gray-300 px-4 py-2">{student.nom}</td>
                        <td className="border border-gray-300 px-4 py-2">{student.prenom}</td>
                        <td className="border border-gray-300 px-4 py-2">{student.dateDeNaissance}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <p>Aucun étudiant dans cette classe.</p>
              )}
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
