import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [students, setStudents] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    console.log("Fichier sélectionné :", e.target.files[0]);
  };

  const handleImport = async () => {
    if (!file) {
      alert("Veuillez sélectionner un fichier");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    console.log("Début de l'import avec le fichier :", file);

    try {
      const response = await fetch("http://localhost:5000/import", {
        method: "POST",
        body: formData,
      });

      console.log("Réponse brute du serveur :", response);

      const data = await response.json();
      if (response.ok) {
        alert("Import réussi !");
        setStudents(data.students); // Met à jour la liste des étudiants
        console.log("Données reçues :", data.students);
      } else {
        alert("Erreur : " + data.message);
      }
    } catch (error) {
      console.error("Erreur lors de l'import :", error);
    }
  };

  return (
    <div className="font-sans bg-gray-100 min-h-screen">
      <nav className="bg-blue-600 p-4 text-white text-center">
        {/* Bouton pour sélectionner un fichier */}
        <label className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded-lg mx-2 cursor-pointer">
          Importer des élèves
          <input
            type="file"
            accept=".csv"
            className="hidden"
            onChange={handleFileChange}
          />
        </label>

  
      </nav>

      {/* Tableau pour afficher les étudiants importés */}
      <div className="container mx-auto mt-4">
        {students.length > 0 ? (
          <table className="table-auto w-full border-collapse border border-gray-400">
            <thead>
              <tr>
                <th className="border border-gray-300 px-4 py-2">Nom</th>
                <th className="border border-gray-300 px-4 py-2">Prénom</th>
                <th className="border border-gray-300 px-4 py-2">
                  Date de naissance
                </th>
              </tr>
            </thead>
            <tbody>
              {students.map((student, index) => (
                <tr key={index}>
                  <td className="border border-gray-300 px-4 py-2">{student.nom}</td>
                  <td className="border border-gray-300 px-4 py-2">{student.prenom}</td>
                  <td className="border border-gray-300 px-4 py-2">
                    {new Date(student.dateDeNaissance).toLocaleDateString("fr-FR")}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-center mt-4 text-gray-500">Aucun élève importé.</p>
        )}
      </div>
    </div>
  );
}

export default App;
