import React, { useState } from "react";
import Papa from "papaparse";
import { parse, isValid, format } from "date-fns";

function App() {
  const [file, setFile] = useState(null);
  const [redoublantsFile, setRedoublantsFile] = useState(null);
  const [data, setData] = useState([]);

  // Fonction générique pour traiter les fichiers CSV
  const handleFileChange = (e, isRedoublant = false) => {
    const selectedFile = e.target.files[0];
    if (isRedoublant) setRedoublantsFile(selectedFile);
    else setFile(selectedFile);

    if (selectedFile) {
      Papa.parse(selectedFile, {
        complete: (result) => processFileData(result.data, isRedoublant),
        header: true,
        skipEmptyLines: true,
      });
    }
  };

  // Traitement des données du fichier
  const processFileData = (data, isRedoublant) => {
    const formattedData = data.map((row) => {
      const rawDate = row["Date de naissance"]?.trim();
      let dateDeNaissance = null;
      
      try {
        dateDeNaissance = isRedoublant
          ? parse(rawDate, "yyyy-MM-dd", new Date())
          : parse(rawDate, "dd/MM/yyyy", new Date());
        
        dateDeNaissance = isValid(dateDeNaissance)
          ? format(dateDeNaissance, "yyyy-MM-dd")
          : null;
      } catch (error) {
        console.error("Erreur de format de date :", rawDate, error);
      }

      return {
        nom: row["Nom"]?.trim(),
        prenom: row["Prenom"]?.trim(),
        dateDeNaissance: dateDeNaissance,
      };
    });

    if (isRedoublant) {
      sendRedoublantsToBackend(formattedData);
      sendRedoublantsFileToBackend(selectedFile);
    } else {
      setData(formattedData);
      sendDataToBackend(formattedData);
    }
  };

  // Envoi des données au backend
  const sendDataToBackend = async (students) => {
    try {
      const response = await fetch("http://localhost:5000/api/students", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ students }),
      });
      const message = response.ok
        ? "Les élèves ont été enregistrés."
        : await response.json();
      alert(message);
    } catch (error) {
      console.error("Erreur d'envoi au serveur:", error);
      alert("Une erreur est survenue.");
    }
  };

  // Envoi des redoublants au backend
  const sendRedoublantsFileToBackend = async (selectedFile) => {
    const formData = new FormData();
    formData.append("redoublants", selectedFile);

    try {
      const response = await fetch("http://localhost:5000/api/update-year", {
        method: "POST",
        body: formData,
      });
      const message = response.ok
        ? "Les redoublants ont été enregistrés et l'année mise à jour."
        : await response.json();
      alert(message);
    } catch (error) {
      console.error("Erreur d'envoi au serveur:", error);
      alert("Une erreur est survenue.");
    }
  };

  // Envoi des données des redoublants au backend
  const sendRedoublantsToBackend = async (redoublants) => {
    try {
      const response = await fetch("http://localhost:5000/api/validate-redoublants", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ redoublants }),
      });
      const message = response.ok
        ? "Les redoublants ont été validés."
        : await response.json();
      alert(message);
    } catch (error) {
      console.error("Erreur d'envoi des redoublants:", error);
      alert("Une erreur est survenue.");
    }
  };

  return (
    <div className="App">
      <h1>Gestion des élèves</h1>
      {/* Import des élèves */}
      <section>
        <h2>Importer un fichier CSV pour les élèves</h2>
        <input
          type="file"
          accept=".csv"
          onChange={(e) => handleFileChange(e, false)}
          className="border p-2 m-4"
        />
      </section>

      {/* Import des redoublants */}
      <section>
        <h2>Indiquer les redoublants</h2>
        <input
          type="file"
          accept=".csv"
          onChange={(e) => handleFileChange(e, true)}
          className="border p-2 m-4"
        />
      </section>

      {/* Affichage des données importées */}
      {data.length > 0 && (
        <table className="table-auto border-collapse border border-gray-400">
          <thead>
            <tr>
              {Object.keys(data[0]).map((header, index) => (
                <th key={index} className="border border-gray-300 px-4 py-2">{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {Object.values(row).map((cell, cellIndex) => (
                  <td key={cellIndex} className="border border-gray-300 px-4 py-2">{cell}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
