import { createBrowserRouter, RouterProvider, Outlet, useNavigate } from 'react-router-dom';
import Auth from "./components/Auth";
import App from "./App";

const Root = () => {
    <Outlet />
};

const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    children: [
      {
        path: "main",
        element: <App />,
      },
      {
        path: "login",
        element: <Auth />,
      },
    ]
  }
]);
const navigate = useNavigate(); // Hook pour naviguer entre les pages
function Main() {
  return (
    <><div className="flex items-center justify-center p-15 min-h-screen">

          <button
              className="w-screen h-screen bg-black bg-opacity-0  text-white text-xl font-bold rounded hover:bg-opacity-10 transition-colors duration-200"
              onClick={() => navigate("/login")}>
              Bienvenue sur Le Petit Vinci ! Votre outil de gestion d'élèves et de classes
          </button>
      </div><RouterProvider router={router} /></>

  );

}

export default Main;
